import styled from 'styled-components';

export const TodoForm = styled.div`
  display: flex;
  /* background-color: #eee; */
  border-radius: 12px;
  gap: 20px;
  padding: 30px;
  text-align: center;
  align-items: center;
  margin: 0 auto;

  margin: 0;
  position: absolute;
  /* top: 50%; */
  left: 50%;
  transform: translate(-50%, -50%);
`;
