import {useNavigate} from 'react-router-dom';

import {useParams, NavLink} from 'react-router-dom';
import './Details.css';
import Buttons from '../TodoItem/Buttons';

const TodoDetails = ({id, inputTitle, input, isDone}) => {
  console.log(id);
  const navigate = useNavigate();

  // 이전 페이지 이동 핸들러
  const previousPageHanlder = () => {
    navigate(-1, true);
  };

  // const {id} = useParams();
  // const todoList = todos.todos.filter((item) => item.id === id.toString());
  return (
    <div className='wrapper'>
      <div className='details_container'>
        <div className='details_top'>
          <div>ID : {id}</div>
          <Buttons onBack={previousPageHanlder} className='return_button' name={'이전으로'}></Buttons>
        </div>
        <div className='details_bottom'>
          <h2>{inputTitle}</h2>
          <p className='todo-context'>{input}</p>
        </div>
      </div>
    </div>
  );
};

export default TodoDetails;

