import styled from 'styled-components';

export const TodoForm = styled.div`
  display: flex;
  justify-content: center;
  background-color: #eee;
  border-radius: 12px;
  padding: 40px;
  margin: 0 auto;
`;

export const InputGroup = styled.div`
  display: flex;
  flex-direction: row;
  gap: 25px;
  align-items: center;
`;

export const ButtonAdd = styled.div`
  background-color: #fa6c6e;
  border: none;
  border-radius: 50px;
  color: #fff;
  font-weight: 700;
  height: 50px;
  width: 50px;
  font-size: 20px;
  /* align-items: center; */
  text-align: center;
  cursor: pointer;
  &:hover {
    background-color: #fb484a;
    scale: 1.1;
    box-shadow: 2px 2px 2px 2px rgba(0, 0, 0, 0.14);
  }
`;

export const TaskInput = styled.div`
  border: none;
  border-radius: 12px;
  height: 50px;
  padding: 0 12px;
  width: 400px;
  font-size: 20px;
`;

export const Title = styled.div`
  font-size: 20px;
  font-weight: 700;
`;
