import styled from 'styled-components';

export const TodoContainer = styled.section`
  border: 4px solid #8385f6;
  border-radius: 12px;
  padding: 0px 25px;
  width: 348px;
  height: 200px;
  /* margin: 30px; */
  background: #5b5ee1;
`;

export const TodoTitle = styled.h2`
  display: flex;
  flex-wrap: wrap;
  gap: 12px;
  font-size: 30px;
`;

export const TodoItem = styled.section`
  display: flex;
  flex-wrap: wrap;
  gap: 12px;
`;

export const TodoListWarp = styled.section`
  background-color: aliceblue;
  /* padding: 0 24px; */
  /* margin: 30px; */
`;
