import styled from 'styled-components';

export const HeaderContainer = styled.div`
  font-weight: bold;
  font-size: 35px;
  text-align: center;
  border: 1px solid #ddd;
  padding: 30px;
  background-color: #5b5ee1;
  color: white;
`;

export const InputGroup = styled.div`
  display: flex;
  flex-direction: row;
  gap: 25px;
  align-items: center;
`;

export const ButtonAdd = styled.div`
  background-color: #fa6c6e;
  border: none;
  border-radius: 50px;
  color: #fff;
  font-weight: 700;
  height: 50px;
  width: 50px;
  font-size: 20px;
  cursor: pointer;
`;

export const TaskInput = styled.div``;

export const Title = styled.div``;
