import './TodoList.css';
import {NavLink} from 'react-router-dom';

import {TodoListWarp, TodoItem, TodoContainer} from './style';

// Todo Î¶¨Ïä§Ìä∏
const TodoList = ({todos, setTodos}) => {
  // ÏôÑÎ£å Î≤ÑÌäºÏù¥ onClickÎêòÎ©¥ Ïã§ÌñâÎêòÎäî Î∂ÄÎ∂Ñ
  const handleComplete = (todo) => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return {...item, isDone: !item.isDone};
        }
        return item;
      })
    );
  };

  // ÏÇ≠Ï†ú Î≤ÑÌäºÏù¥ onClickÎêòÎ©¥ Ïã§ÌñâÎêòÎäî Î∂ÄÎ∂Ñ
  const handleDelete = ({id}) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  // TodosÏôÄ WorkÎäî falseÍ∞íÏùÑ Î∞õÏïÑÏò§Í≥† DoneÏùÄ trueÍ∞íÏùÑ Î∞õÏïÑÏò®Îã§.
  // trueOrFalseÏùò ÏÉÅÌÉúÏóê Îî∞Îùº isDoneÏùÑ ÌôïÏù∏Ìï¥ÏÑú resultDoneÏóê Ï∂îÍ∞ÄÌïòÏó¨ Î∞∞Ïó¥ÏùÑ returnÌïúÎã§.
  const checkDone = (todos, trueOrFalse) => {
    let resultDone = [];
    for (let todo of todos) {
      if (todo.isDone === trueOrFalse) {
        resultDone.push(
          <TodoContainer key={todo.id}>
            <NavLink to={`${todo.id}`}>ÏÉÅÏÑ∏Î≥¥Í∏∞</NavLink>
            <h2 className='todo-title'>ü©∏{todo.title}</h2>
            <div className='todo-context'>{todo.context}</div>
            <div className='buttons'>
              <button className='button-delete button' onClick={() => handleDelete(todo)}>
                ÏÇ≠Ï†úÌïòÍ∏∞
              </button>
              <button className='button-complete button' onClick={() => handleComplete(todo)}>
                {trueOrFalse ? 'Ï∑®ÏÜå' : 'ÏôÑÎ£å'}
              </button>
            </div>
          </TodoContainer>
        );
      }
    }
    return resultDone;
  };

  return (
    <TodoListWarp>
      <h2 className='list-title'>Working..üî•</h2>
      <TodoItem>{checkDone(todos, false)}</TodoItem>
      <h2 className='list-title'>Done..!üéâ</h2>
      <TodoItem>{checkDone(todos, true)}</TodoItem>
    </TodoListWarp>
  );
};

export default TodoList;
