import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import Buttons from './Buttons';
import { toggleDone, deleteTodo } from '../../redux/modules/todos.js';
import { TodoContainer, TodoContext, TodoTitle, TodoListButtons, CheckButtons, DetailsButton } from './style';

const TodoItem = ({ id, inputTitle, input, isDone }) => {
  const dispatch = useDispatch();

  // ÏôÑÎ£å Î≤ÑÌäºÏù¥ onClickÎêòÎ©¥ Ïã§ÌñâÎêòÎäî Î∂ÄÎ∂Ñ
  const toggleDoneHandler = (id) => {
    dispatch(toggleDone({ id }));
  };

  // ÏÇ≠Ï†ú Î≤ÑÌäºÏù¥ onClickÎêòÎ©¥ Ïã§ÌñâÎêòÎäî Î∂ÄÎ∂Ñ
  const deleteTodoHandler = (id) => {
    if (window.confirm('Ìï¥Îãπ Î™©Î°ùÏùÑ Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      dispatch(deleteTodo({ id }));
    }
  };

  return (
    <TodoContainer>
      <TodoTitle>üìç {inputTitle}</TodoTitle>
      <TodoContext>{input}</TodoContext>
      <TodoListButtons>
        <DetailsButton to={`/${id}`}>ÏÉÅÏÑ∏Î≥¥Í∏∞</DetailsButton>
        <CheckButtons>
          <button onClick={() => deleteTodoHandler(id)} placeholder={'ÏÇ≠Ï†ú'} />
          <Buttons onToggle={toggleDoneHandler} id={id} type={'done'} name={isDone ? 'Ï∑®ÏÜå' : 'ÏôÑÎ£å'} />
        </CheckButtons>
      </TodoListButtons>
    </TodoContainer>
  );
};

export default TodoItem;
