import styled from 'styled-components';
import { Link } from 'react-router-dom';

export const TodoContainer = styled.section`
  /* border: 4px solid #8385f6; */
  border-radius: 12px;
  padding: 0px 25px;
  width: 373px;
  height: 230px;
  margin: 20px;
  background-color: #d2d3fb;
`;

export const TodoTitle = styled.h2`
  display: flex;
  flex-wrap: wrap;
  gap: 12px;
  font-size: 30px;
`;

export const TodoContext = styled.div`
  font-size: 20px;
  height: 70px;
  background: #eee;
`;

export const TodoItem = styled.section`
  display: flex;
  flex-wrap: wrap;
  gap: 12px;
`;

export const TodoListButtons = styled.div`
  background-color: aliceblue;
  display: flex;
  justify-content: space-between;
  /* padding: 0 24px; */
  /* margin: 30px; */
`;

export const CheckButtons = styled.div`
  display: flex;
  /* flex-direction: row; */
  gap: 20px;
`;

export const DetailsButton = styled(Link)`
  text-decoration: none;
  color: blue;
`;
