import {Link} from 'react-router-dom';
import {useDispatch} from 'react-redux';
import Buttons from './Buttons';
import {TodoItemWrap, Title, Text, More, ButtonWrap} from './style';
import {toggleDone, deleteTodo} from '../../store/actions';

const TodoItem = ({id, title, content, isDone}) => {
  const dispatch = useDispatch();

  // ÏôÑÎ£å Î≤ÑÌäºÏù¥ onClickÎêòÎ©¥ Ïã§ÌñâÎêòÎäî Î∂ÄÎ∂Ñ
  const handleComplete = (todo) => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return {...item, isDone: !item.isDone};
        }
        return item;
      })
    );
  };

  // Ìà¨Îëê ÏôÑÎ£å/Ï∑®ÏÜå ÌÜ†Í∏Ä Ìï∏Îì§Îü¨
  const toggleDoneHandler = (id) => {
    dispatch(toggleDone({id}));
  };

  // Ìà¨Îëê ÏÇ≠Ï†ú Ìï∏Îì§Îü¨
  const deleteTodoHandler = (id) => {
    if (window.confirm('Ìï¥Îãπ Ìà¨ÎëêÎ•º Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      dispatch(deleteTodo({id}));
    }
  };

  return (
    <TodoItemWrap>
      <Title>üìç {title}</Title>
      <Text>{content}</Text>
      <ButtonWrap>
        <Link to={`/todos/${id}`}>
          <More>ÏÉÅÏÑ∏Î≥¥Í∏∞</More>
        </Link>
        <Buttons onDelete={deleteTodoHandler} id={id} type={'delete'} name={'ÏÇ≠Ï†ú'} />
        <Buttons onToggle={toggleDoneHandler} id={id} type={'done'} name={isDone ? 'Ï∑®ÏÜå' : 'ÏôÑÎ£å'} />
      </ButtonWrap>
    </TodoItemWrap>
  );
};

export default TodoItem;
